import React, { useState } from "react";
import EggAnimation from "./components/egg_animation";
import Navbar from "./components/navbar";
import Gallery from "./components/gallery";
import Message from "./components/message";
import './styles/index.scss';

function App() {
    const [cracked, setCracked] = useState(false);
    const [showGallery, setShowGallery] = useState(false);
    const [showMessage, setShowMessage] = useState(false);

    return (
        <div>
            {/* Always show EggAnimation */}
            <EggAnimation onCrackComplete={() => setCracked(true)} />

            {/* Show navbar and controls once cracked */}
            {cracked && !showGallery && !showMessage && (
                <>
                    <Navbar
                        onGoHome={() => {
                            setShowGallery(false);
                            setShowMessage(false);
                        }}
                        onShowGallery={() => {
                            setShowGallery(true);
                            setShowMessage(false);
                        }}
                        onShowMessage={() => {
                            setShowMessage(true);
                            setShowGallery(false);
                        }}
                    />

                    <div className="side-button-wrapper">
                        <button className="side-button left" onClick={() => setShowGallery(true)}>ðŸ“¸ Gallery</button>
                        <button className="side-button right" onClick={() => setShowMessage(true)}>ðŸ’Œ Message</button>
                    </div>
                </>
            )}

            {/* Show content on top of animation */}
            {cracked && showGallery && <Gallery />}
            {cracked && showMessage && <Message />}
        </div>
    );
}

export default App;
